import React, { Component } from 'react';
import './Button.css';


const STYLES = [
    'btn--primary',
    'btn--outline'


]

const SIZES = [
    'btn--medium',
    'btn--large',

]

export const Button =({
    children,
    type,
    onClick,
    buttonStyle,
    buttonSize

}) => {
    const checkoutButtonStyle = STYLES.includes(buttonStyle) ? buttonStyle : STYLES[0]

    const checkoutButtonSize = SIZES.includes(buttonSize) ? buttonSize : SIZES[0]

     return (
         <button className={`btn ${checkoutButtonStyle} ${checkoutButtonSize}`} onClick={onClick}
         type={type}>
             {children}
         </button>
     )
}
