import React, { Component } from 'react'
import Coverflow from 'react-coverflow';
import { StyleRoot } from 'radium';
import imagem1 from './images/1.jpg';
import imagem2 from './images/2.png';
import imagem3 from './images/3.jpg';


class Coverflows extends Component {

render(){

    return(
  <StyleRoot>
    <Coverflow
      displayQuantityOfSide={2}
      navigation
      infiniteScroll
      enableHeading
      media={{
        '@media (max-width: 900px)': {
          width: '600px',
          height: '300px'
        },
        '@media (min-width: 900px)': {
          width: '960px',
          height: '600px'
        },
        '@media (max-width: 900px)': {
            width: '960px',
            height: '600px'
          },
      }}
    >
      <img src={imagem1} alt='Grupo Vitar Hair' data-action="https://www.grupovitahair.com/"/>
      <img src={imagem2} alt='Loja da Lata' data-action="https://www.lojadalata.com"/>
      <img src={imagem3} alt='Hesheys Company' data-action="https://hersheys.lojadalata.com"/>
      <img src={imagem1} alt='Album one' data-action="https://facebook.github.io/react/"/>
    </Coverflow>
  </StyleRoot>
    );
    }
}

export default Coverflows;
