import ReactDOM from 'react-dom';
import Coverflow from 'react-coverflow';

class Container extends Component {
  constructor(props) {
    super(props);

    this.state = {
      active: 0
    };
  }

  render() {
    return (
      <div>
        <button onClick={this._handleClick.bind(this)}>Radom</button>
        <Coverflow
    width={960}
    height={480}
    displayQuantityOfSide={2}
    navigation={false}
    enableHeading={false}
  >
