  </Coverflow>, 
      </div>
    );
  }

  _handleClick() {
    var num = Math.floor((Math.random() * 10) + 1);
    this.setState({
      active: num
    });
  }
};

ReactDOM.render(
  <Container></Container>,
  document.querySelector('.example_4')
);
