import React from 'react';
import Switch from 'react-bootstrap/esm/Switch';
import {BrowserRouter, Route, Link} from 'react-router-dom';
import './header.css';
import { faHome } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";


const Header = () => (
    <header id="main-header">Sistema React Crud 1.0 &nbsp;

    <a href="/usuarios" type="button" class="btn btn-success">LISTA USUARIOS</a>
    <a href="/criarUsuario" type="button" class="btn btn-primary"><i class="fa fa-plus-circle"></i>CRIAR USUARIO</a>


    </header>


);


const route = (
    <Switch>
    <BrowserRouter>
    <div>
        <Route path="/usuarios" component={Header} exact={true}/>
    </div>
    </BrowserRouter>
    </Switch>
)


export default Header;
