import React from 'react';
import emailjs from 'emailjs-com';

import './contact.css';

export default function ContactUs() {

  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('service_unwkzbo', 'template_8x0hctx', e.target, 'user_KfgjeRuATC8yMbI6chlki')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
      e.target.reset()
  }

  return (

<div class="fh5co-narrow-content animate-box fadeInLeft animated" data-animate-effect="fadeInLeft">

				<div class="row">
					<div class="col-md-4 col-md-offset-1">
						<h2 className="entrecontato">  Entre em contato </h2>
					</div>

				</div>
        <br /><br />
				<form action="">
					<div class="row">
						<div class="col-md-10 col-md-offset-1">
							<div class="row">
								<div class="col-md-6">
									<div class="form-group">
										<input type="text" name="user_name" class="form-control" placeholder="Name"/>
									</div>
									<div class="form-group">
										<input type="text" name="user_email" class="form-control" placeholder="Email"/>
									</div>
									<div class="form-group">
										<input type="text" name="contact_number"  class="form-control" placeholder="Phone"/>
									</div>
								</div>
								<div class="col-md-6">
									<div class="form-group">
										<textarea name="message" cols="30" rows="7" class="form-control" placeholder="Message"></textarea>
									</div>
									<div class="form-group">
										<input type="submit" class="btn btn-primary btn-md" value="Send Message"/>
									</div>
								</div>

							</div>
						</div>

					</div>
				</form>
			</div>

  );
}
